// This file can be used by tests to get the path of CourseProject. This makes
// it much easier to have tests work whether they are run from within their test folder, or
// from alltests.sh

/* Author: Mitchell Ludwig
    mainpath always returns the CourseProject directory
*/

var currDir = process.cwd();
var nextDir = currDir.split("/");
while (nextDir.pop() !== 'src') {
    currDir = nextDir.join("/");
}

module.exports.mainpath = currDir + "/";
